import React, { Component } from "react";
import axios from "axios";
import "antd/dist/antd.css";
import { Input } from 'antd';
import { Popover } from 'antd';

const { Search } = Input;
const onSearch = value => console.log(value);

const content = (
    <div>
        <p>Use factor (^), multiply(*), divide (/), plus (+), and sub (-)</p>
    </div>
);

export default class Katalog extends Component {
    constructor(props) {
        super(props);
        this.state = {
            holidays: [],
            visible: false,
            name: "",
            date: "",
            observed: "",
        };
    }
    handleButton = (name) => {
        alert(name);
    };
    handleName = (e) => {
        this.setState({
            name: e.target.value,
        });
        console.log(this.state.name);
    };
    handleDate = (e) => {
        this.setState({
            date: e.target.value,
        });
        console.log(this.state.date);
    };
    handleObserved = (e) => {
        this.setState({
            result: e.target.value,
        });
        console.log(this.state.result);
    };
    componentDidMount() {
        axios({
            method: "get",
            url: "https://holidayapi.com/v1/holidays?pretty&country=ID-JW&year=2019&key=89b7a895-252c-4930-bc8d-4d7793b3cc85" + this.handleOperation,
            headers: {
                accept: "*/*",
            },
        })
            .then((data) => {
                console.log(data.data);
                this.setState({
                    math: data.data,
                });
            })
            .catch((error) => {
                console.log(error);
            });
    }
    render() {
        return (
            <div>
                <Popover placement="topLeft" content={content} title="Note" trigger="hover">
                    <Search
                        placeholder="Input Math"
                        allowClear
                        enterButton="Get Solution"
                        size="large"
                        onSearch={onSearch}
                    />
                </Popover>
                <br />
                <br />
                <h4>Result:</h4>

            </div>
        );
    }
}
