import React, { Component } from "react";
import axios from "axios";
import "antd/dist/antd.css";
import { Card, Space } from 'antd';
import { Button, notification } from 'antd';

import { Layout, Menu, Breadcrumb } from 'antd';
import style from './style.css';
import { Row, Col } from 'antd';

const { Header, Content, Footer } = Layout;

export default class holidays extends Component {
    constructor(props) {
        super(props);
        this.state = {
            holidays: [],
            visible: false,
            name: "",
            date: "",
            observed: "",
        };
    }
    handleButton = (name, date) => {
        notification.open({
            message: 'Holiday Name :',
            description: name, 
          });
          notification.open({
            message: 'Holiday Date :',
            description: date, 
          });
    };
    handleName = (e) => {
        this.setState({
            name: e.target.value,
        });
        console.log(this.state.name);
    };
    handleDate = (e) => {
        this.setState({
            date: e.target.value,
        });
        console.log(this.state.date);
    };
    handleObserved = (e) => {
        this.setState({
            observed: e.target.value,
        });
        console.log(this.state.observed);
    };
    componentDidMount() {
        axios({
            method: "get",
            url: "https://holidayapi.com/v1/holidays?pretty&country=ID-JW&year=2019&key=89b7a895-252c-4930-bc8d-4d7793b3cc85",
            headers: {
                accept: "*/*",
            },
        })
            .then((data) => {
                console.log(data.data.holidays);
                this.setState({
                    holidays: data.data.holidays,
                });
            })
            .catch((error) => {
                console.log(error);
            });
    }
    render() {
        return (
            <div>
                <div className="boxWhite">
                    {this.state.holidays.map((results, index) => {
                        return (
                            <Layout className="layout">
            <Header>
                <Menu theme="dark" mode="horizontal">
                    <Row gutter={16}>
                        <Col className="gutter-row" span={6}>
                            <div style={style}>Kelompok 22</div>
                        </Col>
                        <Col className="gutter-row" span={6}>
                            <div style={style}></div>
                        </Col>
                        <Col className="gutter-row" span={6}>
                            <div style={style}></div>
                        </Col>
                        <Col className="gutter-row" span={6}>
                            <div style={style}></div>
                        </Col>
                    </Row>
                </Menu>

            </Header>
            <Content style={{ padding: '0 50px' }}>
                <Breadcrumb style={{ margin: '16px 0' }}>
                    <Breadcrumb.Item>Praktikum RSBK</Breadcrumb.Item>
                    <Breadcrumb.Item>Modul 5</Breadcrumb.Item>
                    <Breadcrumb.Item>Tugas</Breadcrumb.Item>
                </Breadcrumb>
                <div className="site-layout-content">
                    <div className="Components">
                        <div className="container">
                            <h2>Holidays in 2019. Not Relevant, Right?</h2>
                            <br />
                            <div key={results.name}>
                                <Space>
                                    <Card title="Holiday" style={{ width: 300 }}>
                                        <p>Name : {results.name}</p>
                                        <p>Date : {results.date}</p>
                                        <p>Observed : {results.observed}</p>
                                        <Button type="primary" onClick={() => this.handleButton(results.name, results.date)}>{" "}Click Me</Button>
                                    </Card>
                                </Space>
                            </div>
                        </div>
                    </div>
                </div>
            </Content>
            <Footer style={{ textAlign: 'center' }}>Ant Design Â©2018 Created by Ant UED</Footer>
        </Layout>
                            
                        );
                    })}
                </div>
            </div>
        );
    }
}
