import React, { Component } from "react";
import axios from "axios";
import { Modal } from "antd";
import "antd/dist/antd.css";
export default class Katalog extends Component {
    constructor(props) {
        super(props);
        this.state = {
            modelos: [],
            visible: false,
            nome: "",
        };
    }
    handleButton = (nama) => {
        alert(nama);
    };
    handleNama = (e) => {
        this.setState({
            nome: e.target.value,
        });
        console.log(this.state.nome);
    };
    // handleNim = (e) => {
    //     this.setState({
    //         nim: e.target.value,
    //     });
    //     console.log(this.state.nim);
    // };
    // handleAsal = (e) => {
    //     this.setState({
    //         asal: e.target.value,
    //     });
    //     console.log(this.state.asal);
    // };
    componentDidMount() {
        axios({
            method: "get",
            url: "https://parallelum.com.br/fipe/api/v1/carros/marcas/156/modelos",
            headers: {
                accept: "*/*",
            },
        })
            .then((data) => {
                console.log(data.data);
                this.setState({
                    modelos: data.data,
                });
            })
            .catch((error) => {
                console.log(error);
            });
    }
    render() {
        return (
            <div>
                <div>
                    
                    {this.state.modelos.map((results, index) => {
                        return (
                            <div className="card" key={results.nama}>
                                <div className="card-body">
                                    <h5 className="card-title">Nama : {results.nome}</h5>
                                </div>
                                <button
                                    className="button"
                                    onClick={() => this.handleButton(results.nome)}
                                >
                                    {" "}
        klik aku
        </button>
                            </div>
                        );
                    })}
                </div>
            </div>
        );
    }
}
