import React, { Component } from "react";
import axios from "axios";
import { Modal } from "antd";
import "antd/dist/antd.css";
export default class tekkom extends Component {
    constructor(props) {
        super(props);
        this.state = {
            tekkom: [],
            visible: false,
            nome: "",
            asal: "",
            nim: "",
        };
    }
    handleButton = (nama) => {
        alert(nama);
    };
    handleTambahOrang = () => {
        this.setState({
            visible: true,
        });
    };
    handleNama = (e) => {
        this.setState({
            nama: e.target.value,
        });
        console.log(this.state.nama);
    };
    handleNim = (e) => {
        this.setState({
            nim: e.target.value,
        });
        console.log(this.state.nim);
    };
    handleAsal = (e) => {
        this.setState({
            asal: e.target.value,
        });
        console.log(this.state.asal);
    };
    componentDidMount() {
        axios({
            method: "get",
            url: "https://backendcatatantugas.herokuapp.com/mahasiswa/all",
            headers: {
                accept: "*/*",
            },
        })
            .then((data) => {
                console.log(data.data);
                this.setState({
                    tekkom: data.data,
                });
            })
            .catch((error) => {
                console.log(error);
            });
    }
    render() {
        return (
            <div>
                <div className="boxWhite">
                    
                    {this.state.tekkom.map((results, index) => {
                        return (
                            <div className="card" key={results.nama}>
                                <div className="card-body">
                                    <h5 className="card-title">Nama : {results.nama}</h5>
                                    <h6 className="card-subtitle mb-2 text-muted">
                                        Nim : {results.nim}
                                    </h6>
                                    <p className="card-text">Asal : {results.asal}</p>
                                </div>
                                <button
                                    className="button"
                                    onClick={() => this.handleButton(results.nama)}
                                >
                                    {" "}
        klik aku
        </button>
                            </div>
                        );
                    })}
                </div>
            </div>
        );
    }
}
