import React, { Component } from "react";
import axios from "axios";
import "antd/dist/antd.css";
import { Input } from 'antd';
import { Popover } from 'antd';

const { Search } = Input;
const onSearch = value => console.log(value);

const content = (
    <div>
        <p>Use factor (^), multiply(*), divide (/), plus (+), and sub (-)</p>
    </div>
);

export default class Katalog extends Component {
    constructor(props) {
        super(props);
        this.state = {
            math: [],
            visible: false,
            operation: "",
            expression: "",
            result: "",
        };
    }
    handleButton = (nome) => {
        alert(nome);
    };
    handleOperation = (e) => {
        this.setState({
            operation: e.target.value,
        });
        console.log(this.state.operation);
    };
    handleExpression = (e) => {
        this.setState({
            expression: e.target.value,
        });
        console.log(this.state.expression);
    };
    handleResult = (e) => {
        this.setState({
            result: e.target.value,
        });
        console.log(this.state.result);
    };
    handleSubmit = () => {
        if (
            this.state.operation !== "" &&
            this.state.expression !== "" &&
            !this.state.result !== ""
        ) {
            axios({
                method: "post",
                url: "https://backendcatatantugas.herokuapp.com/mahasiswa/add",
                headers: {
                    accept: "*/*",
                },
                data: {
                    nama: this.state.nama,
                    nim: this.state.nim,
                    asal: this.state.asal,
                },
            })
                .then((data) => {
                    alert("berhasil menambahkan");
                    window.location.reload();
                })
                .catch((error) => {
                    alert("gagal lur");
                });
        } else {
            alert("pastikan semua kolom terisi");
        }
    };
    componentDidMount() {
        axios({
            method: "get",
            url: "https://newton.now.sh/api/v2/simplyfy/" + this.handleOperation,
            headers: {
                accept: "*/*",
            },
        })
            .then((data) => {
                console.log(data.data);
                this.setState({
                    math: data.data,
                });
            })
            .catch((error) => {
                console.log(error);
            });
    }
    render() {
        return (
            <div>
                <div className="boxWhite">

                    {this.state.math.map((results, index) => {
                        return (
                            <div>
                                <Popover placement="topLeft" content={content} title="Note" trigger="hover">
                                    <Search
                                        placeholder="Input Math"
                                        allowClear
                                        enterButton="Get Solution"
                                        size="large"
                                        onSearch={onSearch}
                                    />
                                </Popover>
                                <br />
                                <br />
                                <h4>Result:</h4>

                            </div>
                        );
                    })}
                </div>
            </div>
        );
    }
}
